mapper.insert:

	
		
			SELECT BBS_BOARD_S.NEXTVAL as BBS_BOARD_ID
		  	  FROM DUAL
		
		
			SELECT #{BBS_BOARD_ID} as BBS_BOARD_ID
		  	  FROM DUAL
		
	

/* P_BBS_BOARD */
MERGE INTO BBS_BOARD
	 USING DUAL
	    ON (BBS_BOARD_ID = #{BBS_BOARD_ID})
	  WHEN MATCHED THEN
	UPDATE SET CATEGORY_CODE 	= #{CATEGORY_CODE}
			 , TITLE			= #{TITLE}
			 , BOARD_CONTENTS	= #{BOARD_CONTENTS}
			 , MID = #{MID}
			 , MDT = SYSDATE
			 , MIP = #{MIP}
	  WHEN NOT MATCHED THEN
	  INSERT(BBS_BOARD_ID
          , MODULE_CODE
          , CATEGORY_CODE
          , BOARD_NO
          , TITLE
          , BOARD_CONTENTS
          , VIEW_CNT
          
          )
       VALUES(#{BBS_BOARD_ID}
       	  , #{MODULE_CODE}
          , #{CATEGORY_CODE}
          , (SELECT NVL(MAX(BOARD_NO), 0)
               FROM BBS_BOARD
              WHERE MODULE_CODE = #{MODULE_CODE}) +1
          , #{TITLE}
          , #{BOARD_CONTENTS}
          , 0
          , #{MID}
          , SYSDATE
          , #{MIP}
          , #{MID}
          , SYSDATE    
          , #{MIP})

mapper.select:
/* S_BBS_MODULE */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.DETAIL_CODE AS MODULE_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS MODULE_NAME FROM COMM_CODE_DETAIL A JOIN COMM_CODE_MASTER B ON A.COMM_CODE_MASTER_ID = B.COMM_CODE_MASTER_ID WHERE B.USE_YN = '1' AND B.SYSTEM_CODE_YN = '1' AND B.MASTER_CODE = 'MODULE' AND A.ATTRIBUTE1 = '1'
AND (TO_DATE(A.START_DATE) <= SYSDATE AND NVL(TO_DATE(A.END_DATE), SYSDATE) <= SYSDATE)
ORDER BY A.SEQ ASC

/* S_BBS_BOARD_ONE */
SELECT A.BBS_BOARD_ID  
	 , A.MODULE_CODE   
	 , A.CATEGORY_CODE 
	 , A.BOARD_NO      
	 , A.TITLE         
	 , A.BOARD_CONTENTS
  FROM bbs_board A
 WHERE A.BBS_BOARD_ID = #{BBS_BOARD_ID}
   AND A.MODULE_CODE = #{MODULE_CODE}
   AND A.BOARD_NO = #{BOARD_NO}

/* S_BBS_BOARD */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.BBS_BOARD_ID , A.MODULE_CODE , A.CATEGORY_CODE , A.BOARD_NO , A.TITLE , COMM_UTIL_PKG.user_name(A.CID) AS WRITER , A.VIEW_CNT FROM BBS_BOARD A WHERE A.MODULE_CODE = #{MODULE_CODE} AND A.CATEGORY_CODE = #{CATEGORY_CODE}
AND instr(COMM_UTIL_PKG.user_name(A.CID), #{WRITER}) > 0
AND instr(A.TITLE, #{TITLE}) > 0
ORDER BY A.BOARD_NO DESC
