mapper.insert:

	
		
			SELECT BBS_BOARD_S.NEXTVAL as BBS_BOARD_ID
		  	  FROM DUAL
		
		
			SELECT #{BBS_BOARD_ID} as BBS_BOARD_ID
		  	  FROM DUAL
		
	

/* P_BBS_BOARD */
MERGE INTO BBS_BOARD
	 USING DUAL
	    ON (BBS_BOARD_ID = #{BBS_BOARD_ID})
	  WHEN MATCHED THEN
	UPDATE SET CATEGORY_CODE 	= #{CATEGORY_CODE}
			 , TITLE			= #{TITLE}
			 , BOARD_CONTENTS	= #{BOARD_CONTENTS}
			 , MID = #{MID}
			 , MDT = SYSDATE
			 , MIP = #{MIP}
	  WHEN NOT MATCHED THEN
	  INSERT(BBS_BOARD_ID
          , MODULE_CODE
          , CATEGORY_CODE
          , BOARD_NO
          , TITLE
          , BOARD_CONTENTS
          , HIT_CNT
          
          )
       VALUES(#{BBS_BOARD_ID}
       	  , #{MODULE_CODE}
          , #{CATEGORY_CODE}
          , (SELECT NVL(MAX(BOARD_NO), 0)
               FROM BBS_BOARD
              WHERE MODULE_CODE = #{MODULE_CODE}) +1
          , #{TITLE}
          , #{BOARD_CONTENTS}
          , 0
          , #{MID}
          , SYSDATE
          , #{MIP}
          , #{MID}
          , SYSDATE    
          , #{MIP})

mapper.select:
/* S_BBS_MODULE */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.DETAIL_CODE AS MODULE_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS MODULE_NAME FROM COMM_CODE_DETAIL A JOIN COMM_CODE_MASTER B ON A.COMM_CODE_MASTER_ID = B.COMM_CODE_MASTER_ID WHERE B.USE_YN = '1' AND B.SYSTEM_CODE_YN = '1' AND B.MASTER_CODE = 'MODULE' AND A.ATTRIBUTE1 = '1'
AND (TO_DATE(A.START_DATE) <= SYSDATE AND NVL(TO_DATE(A.END_DATE), SYSDATE) <= SYSDATE)
ORDER BY A.SEQ ASC
