mapper.insert:
/* P_MLG_BATCH_REGIST */ DECLARE BEGIN
FOR CS IN ( select A.${MLG_COLUMN} AS MLG_CODE from ${TABLE_NAME} A WHERE NOT EXISTS ( SELECT COMM_MLG_ID FROM COMM_MLG Z WHERE Z.MLG_CODE = A.${MLG_COLUMN} AND Z.${COMPARE_COLUMN} = '1' ) ) LOOP
MERGE INTO COMM_MLG A USING DUAL B ON (A.MLG_CODE = CS.MLG_CODE) WHEN MATCHED THEN UPDATE SET ${COMPARE_COLUMN} = '1' , MID = #{MID} , MDT = SYSDATE , MIP = #{MIP} WHEN NOT MATCHED THEN INSERT(COMM_MLG_ID ,MLG_CODE ,MLG_KO ,${COMPARE_COLUMN} ,CID ,CDT ,CIP ,MID ,MDT ,MIP) VALUES(COMM_MLG_S.NEXTVAL ,CS.MLG_CODE ,REPLACE(CS.MLG_CODE, '_', ' ') ,'1' ,#{CID} ,SYSDATE ,#{CIP} ,#{MID} ,SYSDATE ,#{MIP}) ;
END LOOP; END;

mapper.select:
/* S_COMM_GRID_CONTEXT */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.DETAIL_CODE AS FUNCTION_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS FUNCTION_NAME , NVL(B.USE_YN, 0) AS USE_YN , B.DESCRIPTION , B.COMM_GRID_CONTEXT_ID , #{COMM_GRID_MASTER_ID} AS COMM_GRID_MASTER_ID FROM COMM_CODE_DETAIL A JOIN COMM_CODE_MASTER A2 ON A.COMM_CODE_MASTER_ID = A2.COMM_CODE_MASTER_ID LEFT JOIN COMM_GRID_CONTEXT B ON A.DETAIL_CODE = B.FUNCTION_CODE AND B.COMM_GRID_MASTER_ID = #{COMM_GRID_MASTER_ID} WHERE A2.SYSTEM_CODE_YN = '1' AND A2.USE_YN = '1' AND A2.MASTER_CODE = 'GRID_CONTEXT'
AND (TO_DATE(A.START_DATE) <= SYSDATE AND NVL(TO_DATE(A.END_DATE), SYSDATE) <= SYSDATE)
ORDER BY A.SEQ

/* S_COMM_GRID_CONTEXT_DATA */ SELECT C.GRID_NAME , LISTAGG(A.FUNCTION_CODE, ',') WITHIN GROUP(ORDER BY B.SEQ) AS CONTEXT_MENU FROM COMM_GRID_CONTEXT A LEFT JOIN ( SELECT Y.DETAIL_CODE , Y.START_DATE , Y.END_DATE , Y.SEQ FROM COMM_CODE_MASTER Z JOIN COMM_CODE_DETAIL Y ON Z.COMM_CODE_MASTER_ID = Y.COMM_CODE_MASTER_ID WHERE Z.SYSTEM_CODE_YN = '1' AND Z.USE_YN = '1' AND Z.MASTER_CODE = 'GRID_CONTEXT' ) B ON A.FUNCTION_CODE = B.DETAIL_CODE LEFT JOIN COMM_GRID_MASTER C ON A.COMM_GRID_MASTER_ID = C.COMM_GRID_MASTER_ID WHERE A.USE_YN = '1'
AND (TO_DATE(B.START_DATE) <= SYSDATE AND SYSDATE <= NVL(TO_DATE(B.END_DATE), SYSDATE))
AND A.COMM_GRID_MASTER_ID = #{COMM_GRID_MASTER_ID} GROUP BY C.GRID_NAME

/* S_GRID_DATA */ SELECT C.GRID_NAME , C.FILTER_YN , C.SORT_YN , C.TREE_YN , A.SEQ , A.FIELD , A.MLG_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS FIELD_NAME , A.GROUP_MLG_CODE , A.WIDTH , A.TEXT_ALIGN , CASE WHEN A.FIELD_TYPE = 'COMBO' AND A.QUERY_ID IS NULL THEN 'EDIT' ELSE A.FIELD_TYPE END AS FIELD_TYPE , A.REQUIRE_YN , A.FIXED_YN , A.USE_YN , A.FOOTER , A.DESCRIPTION , A.QUERY_ID , A.COMM_GRID_DETAIL_ID , B.EDITOR , B.PASTE_YN FROM COMM_GRID_DETAIL A LEFT JOIN ( SELECT Y.DETAIL_CODE , Y.START_DATE , Y.END_DATE , Y.SEQ , Y.ATTRIBUTE1 AS EDITOR , Y.ATTRIBUTE2 AS PASTE_YN FROM COMM_CODE_MASTER Z JOIN COMM_CODE_DETAIL Y ON Z.COMM_CODE_MASTER_ID = Y.COMM_CODE_MASTER_ID WHERE Z.SYSTEM_CODE_YN = '1' AND Z.USE_YN = '1' AND Z.MASTER_CODE = 'GRID_FIELD_TYPE'
AND (TO_DATE(Y.START_DATE) <= SYSDATE AND SYSDATE <= NVL(TO_DATE(Y.END_DATE), SYSDATE))
) B ON CASE WHEN A.FIELD_TYPE = 'COMBO' AND A.QUERY_ID IS NULL THEN 'EDIT' ELSE A.FIELD_TYPE END = B.DETAIL_CODE LEFT JOIN COMM_GRID_MASTER C ON A.COMM_GRID_MASTER_ID = C.COMM_GRID_MASTER_ID WHERE C.COMM_GRID_MASTER_ID = #{COMM_GRID_MASTER_ID} AND A.USE_YN = '1' ORDER BY A.SEQ

/* S_COMM_CODE_MASTER */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.COMM_CODE_MASTER_ID , A.MASTER_CODE , A.MLG_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS CODE_NAME , NVL(A.USE_YN, '0') AS USE_YN , NVL(A.SYSTEM_CODE_YN, '0') AS SYSTEM_CODE_YN , A.DESCRIPTION , A.ATTRIBUTE1 , A.ATTRIBUTE2 , A.ATTRIBUTE3 , A.ATTRIBUTE4 , A.ATTRIBUTE5 , A.ATTRIBUTE6 , A.ATTRIBUTE7 , A.ATTRIBUTE8 , A.ATTRIBUTE9 , A.ATTRIBUTE10 FROM COMM_CODE_MASTER A WHERE A.COMM_CODE_MASTER_ID = A.COMM_CODE_MASTER_ID
AND ( instr(A.MASTER_CODE, #{p_masterCode}) > 0 OR instr(COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}), #{p_masterCode}) > 0 )
ORDER BY A.COMM_CODE_MASTER_ID DESC

/* S_MENU_URL */
SELECT A.MENU_URL
  FROM COMM_MENU A
 WHERE A.USE_YN = '1'
   AND A.MENU_CODE = #{MENU_CODE}
   AND #{CID} = #{CID}

/* S_COMM_MLG_TYPE */
SELECT COMM_MLG_ID
 	 , MLG_CODE
 	 , MLG_KO
 	 , MLG_EN
 	 , NVL(MSG_YN, '0') 	AS MSG_YN
 	 , NVL(MENU_YN, '0')	AS MENU_YN
 	 , NVL(CODE_YN, '0')	AS CODE_YN
 	 , NVL(GRID_YN, '0')	AS GRID_YN
 	 , NVL(FUNCTION_YN, '0')	AS FUNCTION_YN
 	 , DESCRIPTION
  FROM COMM_MLG
 WHERE COMM_MLG_ID = COMM_MLG_ID
 
 	AND MENU_YN = #{MENU_YN}
 
 
 	AND MSG_YN = #{MSG_YN}
 
 
 	AND CODE_YN = #{CODE_YN}
 
 
 	AND GRID_YN = #{GRID_YN}
 
 
 	AND FUNCTION_YN = #{FUNCTION_YN}
 
 ORDER BY MLG_KO

/* S_COMM_MLG */ SELECT 'id_' || (ROWNUM-1) AS "id" , COMM_MLG_ID , MLG_CODE , MLG_KO , MLG_EN , NVL(MSG_YN, '0') AS MSG_YN , NVL(MENU_YN, '0') AS MENU_YN , NVL(CODE_YN, '0') AS CODE_YN , NVL(GRID_YN, '0') AS GRID_YN , NVL(FUNCTION_YN, '0') AS FUNCTION_YN , DESCRIPTION FROM COMM_MLG WHERE COMM_MLG_ID = COMM_MLG_ID
AND ( instr(MLG_CODE, #{p_mlgCode}) > 0 OR instr(MLG_KO, #{p_mlgCode}) > 0 )
ORDER BY COMM_MLG_ID DESC

/* S_COLUMNS */
SELECT COLUMN_NAME
  FROM COLS 
 WHERE TABLE_NAME = #{TALBE_NAME}
 ORDER BY COLUMN_ID ASC

/* S_SALT */
SELECT A.SALT
  FROM COMM_USER_SALT A
  JOIN COMM_USER B
    ON A.COMM_USER_ID = B.COMM_USER_ID
 WHERE B.LOGIN_ID = #{LOGIN_ID}

/* S_LOGIN */
SELECT A.COMM_USER_ID
	 , A.LOGIN_ID
 	 , A.USER_NAME
 	 , NVL(A.USE_YN, '0')	AS USE_YN
 	 , TO_CHAR(A.CDT, 'YYYY-MM-DD') as CDT
  FROM COMM_USER A
 WHERE A.USE_YN = #{USE_YN}
   AND A.LOGIN_ID = #{LOGIN_ID}
   AND A.PWD = #{PWD}

mapper.insert:
/* I_COMM_USER */

	SELECT COMM_USER_S.NEXTVAL as COMM_USER_ID
	  FROM DUAL

INSERT INTO COMM_USER(COMM_USER_ID 
					, LOGIN_ID 
					, PWD 
					, USER_NAME 
					, USE_YN
					
					)
			  VALUES( #{COMM_USER_ID}
					, #{LOGIN_ID}
					, #{PWD}
					, #{USER_NAME}
					, '1'
					, #{COMM_USER_ID}
					, SYSDATE
					, #{CIP}
					, #{COMM_USER_ID}
					, SYSDATE
					, #{MIP}
					)

/* I_SALT */

	SELECT COMM_USER_SALT_S.NEXTVAL as "COMM_USER_SALT_ID"
	  FROM DUAL

INSERT INTO COMM_USER_SALT(COMM_USER_SALT_ID
						 , COMM_USER_ID 
						 , SALT
						 
						  )
				   VALUES( #{COMM_USER_SALT_ID} 
						 , #{COMM_USER_ID}
						 , #{SALT}
						 , #{COMM_USER_ID} 
						 , SYSDATE
						 , #{CIP}
						 , #{COMM_USER_ID} 
						 , SYSDATE
						 , #{MIP}
						 )

mapper.select:
/* S_COMM_USER */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.LOGIN_ID AS LOGIN_ID , A.USER_NAME AS USER_NAME , A.USE_YN AS USE_YN , A.LOCK_YN AS LOCK_YN , TO_CHAR(A.CDT, 'YYYY-MM-DD') AS CDT , A.COMM_USER_ID AS COMM_USER_ID FROM COMM_USER A WHERE A.COMM_USER_ID = A.COMM_USER_ID
AND ( instr(A.LOGIN_ID, #{p_loginId}) > 0 OR instr(A.USER_NAME, #{p_loginId}) > 0 )
ORDER BY A.CDT DESC

mapper.update:
/* U_COMM_USER */
UPDATE COMM_USER
   SET USE_YN = #{USE_YN}
     , LOCK_YN = #{LOCK_YN}
   	 , MID = #{MID}
   	 , MDT = SYSDATE
   	 , MIP = #{MIP}
 WHERE COMM_USER_ID = #{COMM_USER_ID}
