mapper.select:
/* S_COMM_FUNCTION */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.COMM_FUNCTION_ID , A.FUNCTION_CODE , A.MLG_CODE , A.DESCRIPTION , A.ATTRIBUTE1 , A.ATTRIBUTE2 , A.ATTRIBUTE3 , A.ATTRIBUTE4 , A.ATTRIBUTE5 , A.ATTRIBUTE6 , A.ATTRIBUTE7 , A.ATTRIBUTE8 , A.ATTRIBUTE9 , A.ATTRIBUTE10 FROM COMM_FUNCTION A WHERE A.COMM_FUNCTION_ID = A.COMM_FUNCTION_ID
AND ( instr(A.FUNCTION_CODE, #{p_functionCode}) > 0 OR instr(COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}), #{p_functionCode}) > 0 )
ORDER BY A.MLG_CODE ASC

/* S_LAST_MENU_OPEN_LIST */ SELECT A.MENU_CODE , A.MENU_URL , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS MENU_NAME , TO_CHAR(A.OPEN_DATE, 'YYYY-MM-DD HH:MI:SS') AS OPEN_DATE FROM ( SELECT B.MENU_CODE AS MENU_CODE , B.MLG_CODE AS MLG_CODE , B.MENU_URL AS MENU_URL , MAX(A.MDT) AS OPEN_DATE FROM COMM_MENU_OPEN_HIST A JOIN COMM_MENU B ON A.COMM_MENU_ID = B.COMM_MENU_ID WHERE B.USE_YN = '1' AND A.COMM_USER_ID = #{CID}
AND ( instr(B.MENU_CODE, 'HIDDEN') = 0 OR instr(B.UP_MENU_CODE, 'HIDDEN') = 0 )
GROUP BY B.MENU_CODE, B.MENU_URL, B.MLG_CODE ORDER BY MAX(A.MDT) DESC ) A WHERE 1=1
AND ROWNUM < 8

mapper.insert:
