mapper.select:
/* S_COMM_TABLE */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.TABLE_NAME AS TABLE_NAME , B.COMMENTS AS COMMENTS , C.LAST_NUMBER AS SEQ_CURRVAL FROM ALL_TABLES A JOIN ALL_TAB_COMMENTS B ON A.TABLE_NAME = B.TABLE_NAME AND B.TABLE_TYPE = 'TABLE' LEFT JOIN USER_SEQUENCES C ON A.TABLE_NAME || '_S' = C.SEQUENCE_NAME WHERE A.OWNER = 'PRJB'
AND ( instr( upper(A.TABLE_NAME), upper(#{p_tableNm}) ) > 0 OR instr( upper(B.COMMENTS), upper(#{p_tableNm}) ) > 0 )
ORDER BY A.TABLE_NAME ASC

mapper.insert:
/* P_MLG_BATCH_REGIST */ DECLARE BEGIN
FOR CS IN ( select A.${MLG_COLUMN} AS MLG_CODE from ${TABLE_NAME} A WHERE NOT EXISTS ( SELECT COMM_MLG_ID FROM COMM_MLG Z WHERE Z.MLG_CODE = A.${MLG_COLUMN} AND Z.${COMPARE_COLUMN} = '1' ) ) LOOP
MERGE INTO COMM_MLG A USING DUAL B ON (A.MLG_CODE = CS.MLG_CODE) WHEN MATCHED THEN UPDATE SET ${COMPARE_COLUMN} = '1' , MID = #{MID} , MDT = SYSDATE , MIP = #{MIP} WHEN NOT MATCHED THEN INSERT(COMM_MLG_ID ,MLG_CODE ,MLG_KO ,${COMPARE_COLUMN}
) VALUES(COMM_MLG_S.NEXTVAL ,CS.MLG_CODE ,REPLACE(CS.MLG_CODE, '_', ' ') ,'1' ,#{CID} ,SYSDATE ,#{CIP} ,#{MID} ,SYSDATE ,#{MIP}) ;
END LOOP; END;

mapper.select:
/* S_COMM_GRID_CONTEXT */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.DETAIL_CODE AS FUNCTION_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS FUNCTION_NAME , NVL(B.USE_YN, 0) AS USE_YN , B.DESCRIPTION , B.COMM_GRID_CONTEXT_ID , #{COMM_GRID_MASTER_ID} AS COMM_GRID_MASTER_ID FROM COMM_CODE_DETAIL A JOIN COMM_CODE_MASTER A2 ON A.COMM_CODE_MASTER_ID = A2.COMM_CODE_MASTER_ID LEFT JOIN COMM_GRID_CONTEXT B ON A.DETAIL_CODE = B.FUNCTION_CODE AND B.COMM_GRID_MASTER_ID = #{COMM_GRID_MASTER_ID} WHERE A2.SYSTEM_CODE_YN = '1' AND A2.USE_YN = '1' AND A2.MASTER_CODE = 'GRID_CONTEXT'
AND (TO_DATE(A.START_DATE) <= SYSDATE AND NVL(TO_DATE(A.END_DATE), SYSDATE) <= SYSDATE)
ORDER BY A.SEQ

/* S_COMM_GRID_CONTEXT_DATA */ SELECT C.GRID_NAME , LISTAGG(A.FUNCTION_CODE, ',') WITHIN GROUP(ORDER BY B.SEQ) AS CONTEXT_MENU FROM COMM_GRID_CONTEXT A LEFT JOIN ( SELECT Y.DETAIL_CODE , Y.START_DATE , Y.END_DATE , Y.SEQ FROM COMM_CODE_MASTER Z JOIN COMM_CODE_DETAIL Y ON Z.COMM_CODE_MASTER_ID = Y.COMM_CODE_MASTER_ID WHERE Z.SYSTEM_CODE_YN = '1' AND Z.USE_YN = '1' AND Z.MASTER_CODE = 'GRID_CONTEXT' ) B ON A.FUNCTION_CODE = B.DETAIL_CODE LEFT JOIN COMM_GRID_MASTER C ON A.COMM_GRID_MASTER_ID = C.COMM_GRID_MASTER_ID JOIN ( SELECT C.COMM_GRID_CONTEXT_ID , MAX(C.USE_YN) AS USE_YN FROM COMM_AUTH_GROUP A JOIN COMM_AUTH_GROUP_USER B ON A.COMM_AUTH_GROUP_ID = B.COMM_AUTH_GROUP_ID JOIN COMM_AUTH_GROUP_CONTEXT C ON A.COMM_AUTH_GROUP_ID = C.COMM_AUTH_GROUP_ID WHERE B.COMM_USER_ID = #{CID} GROUP BY C.COMM_GRID_CONTEXT_ID ) D ON D.COMM_GRID_CONTEXT_ID = A.COMM_GRID_CONTEXT_ID WHERE A.USE_YN = '1' AND D.USE_YN = '1'
AND (TO_DATE(B.START_DATE) <= SYSDATE AND SYSDATE <= NVL(TO_DATE(B.END_DATE), SYSDATE))
AND A.COMM_GRID_MASTER_ID = #{COMM_GRID_MASTER_ID} GROUP BY C.GRID_NAME

/* S_GRID_DATA */ SELECT C.GRID_NAME , C.FILTER_YN , C.SORT_YN , C.TREE_YN , C.ATTRIBUTE1 , A.SEQ , A.FIELD , A.MLG_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS FIELD_NAME , A.GROUP_MLG_CODE , A.WIDTH , A.TEXT_ALIGN , CASE WHEN A.FIELD_TYPE = 'COMBO' AND A.QUERY_ID IS NULL THEN 'EDIT' ELSE A.FIELD_TYPE END AS FIELD_TYPE , A.REQUIRE_YN , A.FIXED_YN , A.USE_YN , A.FOOTER , A.DESCRIPTION , A.QUERY_ID , A.COMM_GRID_DETAIL_ID , B.EDITOR , B.PASTE_YN FROM COMM_GRID_DETAIL A LEFT JOIN ( SELECT Y.DETAIL_CODE , Y.START_DATE , Y.END_DATE , Y.SEQ , Y.ATTRIBUTE1 AS EDITOR , Y.ATTRIBUTE2 AS PASTE_YN FROM COMM_CODE_MASTER Z JOIN COMM_CODE_DETAIL Y ON Z.COMM_CODE_MASTER_ID = Y.COMM_CODE_MASTER_ID WHERE Z.SYSTEM_CODE_YN = '1' AND Z.USE_YN = '1' AND Z.MASTER_CODE = 'GRID_FIELD_TYPE'
AND (TO_DATE(Y.START_DATE) <= SYSDATE AND SYSDATE <= NVL(TO_DATE(Y.END_DATE), SYSDATE))
) B ON CASE WHEN A.FIELD_TYPE = 'COMBO' AND A.QUERY_ID IS NULL THEN 'EDIT' ELSE A.FIELD_TYPE END = B.DETAIL_CODE LEFT JOIN COMM_GRID_MASTER C ON A.COMM_GRID_MASTER_ID = C.COMM_GRID_MASTER_ID WHERE C.COMM_GRID_MASTER_ID = #{COMM_GRID_MASTER_ID} AND A.USE_YN = '1' ORDER BY A.SEQ

/* S_COMM_GRID_MASTER */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.COMM_GRID_MASTER_ID , A.MENU_CODE , COMM_UTIL_PKG.lang(B.MLG_CODE, #{LANG_CODE}) AS MENU_NAME , A.GRID_NAME , A.FILTER_YN , A.DESCRIPTION , A.SORT_YN , A.TREE_YN , A.ATTRIBUTE1 FROM COMM_GRID_MASTER A LEFT JOIN COMM_MENU B ON A.MENU_CODE = B.MENU_CODE WHERE A.COMM_GRID_MASTER_ID = A.COMM_GRID_MASTER_ID
AND A.MENU_CODE = #{MENU_CODE}
AND B.COMM_MENU_ID = #{COMM_MENU_ID}

/* S_COMM_CODE_MASTER */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.COMM_CODE_MASTER_ID , A.MASTER_CODE , A.MLG_CODE , COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE}) AS CODE_NAME , NVL(A.USE_YN, '0') AS USE_YN , NVL(A.SYSTEM_CODE_YN, '0') AS SYSTEM_CODE_YN , A.DESCRIPTION , A.ATTRIBUTE1 , A.ATTRIBUTE2 , A.ATTRIBUTE3 , A.ATTRIBUTE4 , A.ATTRIBUTE5 , A.ATTRIBUTE6 , A.ATTRIBUTE7 , A.ATTRIBUTE8 , A.ATTRIBUTE9 , A.ATTRIBUTE10 FROM COMM_CODE_MASTER A WHERE A.COMM_CODE_MASTER_ID = A.COMM_CODE_MASTER_ID
AND ( instr(upper(A.MASTER_CODE), upper(#{p_masterCode})) > 0 OR instr(upper(COMM_UTIL_PKG.lang(A.MLG_CODE, #{LANG_CODE})), upper(#{p_masterCode})) > 0 )
ORDER BY A.COMM_CODE_MASTER_ID DESC

/* S_MY_MENU */ WITH TEMP AS( SELECT LEVEL-1 AS MENU_DEPTH , SYS_CONNECT_BY_PATH(A.MENU_CODE, '@') AS MENU_PATH , A.COMM_MENU_ID , A.MENU_CODE , A.MLG_CODE , A.MENU_URL , A.UP_MENU_CODE , A.USE_YN , A.DESCRIPTION , A.SEQ , A.ATTRIBUTE1 FROM COMM_MENU A JOIN ( SELECT C.COMM_MENU_ID , MAX(C.USE_YN) AS USE_YN FROM COMM_AUTH_GROUP A JOIN COMM_AUTH_GROUP_USER B ON A.COMM_AUTH_GROUP_ID = B.COMM_AUTH_GROUP_ID JOIN COMM_AUTH_GROUP_MENU C ON A.COMM_AUTH_GROUP_ID = C.COMM_AUTH_GROUP_ID WHERE B.COMM_USER_ID = #{CID} GROUP BY C.COMM_MENU_ID ) B ON A.COMM_MENU_ID = B.COMM_MENU_ID WHERE A.USE_YN = '1' AND B.USE_YN = '1'
AND ( instr(A.MENU_CODE, 'HIDDEN') = 0 OR instr(A.UP_MENU_CODE, 'HIDDEN') = 0 )
START WITH UP_MENU_CODE IS null CONNECT BY PRIOR MENU_CODE = UP_MENU_CODE ORDER SIBLINGS BY SEQ ) SELECT A.* , ( SELECT CASE WHEN COUNT(0) > 0 THEN '1' ELSE '0' END FROM TEMP WHERE UP_MENU_CODE = A.MENU_CODE ) AS CHILD_YN FROM TEMP A

/* S_COMM_MLG_TYPE */
SELECT COMM_MLG_ID
 	 , MLG_CODE
 	 , MLG_KO
 	 , MLG_EN
 	 , NVL(MSG_YN, '0') 	AS MSG_YN
 	 , NVL(MENU_YN, '0')	AS MENU_YN
 	 , NVL(CODE_YN, '0')	AS CODE_YN
 	 , NVL(GRID_YN, '0')	AS GRID_YN
 	 , NVL(FUNCTION_YN, '0')	AS FUNCTION_YN
 	 , DESCRIPTION
 	 , MSG_TYPE_CODE
  FROM COMM_MLG
 WHERE COMM_MLG_ID = COMM_MLG_ID
 
 	AND MENU_YN = #{MENU_YN}
 
 
 	AND MSG_YN = #{MSG_YN}
 
 
 	AND CODE_YN = #{CODE_YN}
 
 
 	AND GRID_YN = #{GRID_YN}
 
 
 	AND FUNCTION_YN = #{FUNCTION_YN}
 
 ORDER BY MLG_KO

/* S_COMM_MLG */ SELECT 'id_' || (ROWNUM-1) AS "id" , COMM_MLG_ID , MLG_CODE , MLG_KO , MLG_EN , NVL(MSG_YN, '0') AS MSG_YN , NVL(MENU_YN, '0') AS MENU_YN , NVL(CODE_YN, '0') AS CODE_YN , NVL(GRID_YN, '0') AS GRID_YN , NVL(FUNCTION_YN, '0') AS FUNCTION_YN , DESCRIPTION , MSG_TYPE_CODE FROM COMM_MLG WHERE COMM_MLG_ID = COMM_MLG_ID
AND ( instr(MLG_CODE, #{p_mlgCode}) > 0 OR instr(MLG_KO, #{p_mlgCode}) > 0 )
ORDER BY COMM_MLG_ID DESC

/* S_COLUMNS */
SELECT COLUMN_NAME
  FROM COLS 
 WHERE TABLE_NAME = #{TALBE_NAME}
 ORDER BY COLUMN_ID ASC

/* S_SALT */
SELECT A.SALT
  FROM COMM_USER_SALT A
  JOIN COMM_USER B
    ON A.COMM_USER_ID = B.COMM_USER_ID
 WHERE B.LOGIN_ID = #{LOGIN_ID}

/* S_LOGIN */
SELECT A.COMM_USER_ID
	 , A.LOGIN_ID
 	 , A.USER_NAME
 	 , NVL(A.USE_YN, '0')	AS USE_YN
 	 , TO_CHAR(A.CDT, 'YYYY-MM-DD') as CDT
  FROM COMM_USER A
 WHERE A.USE_YN = #{USE_YN}
   AND A.LOGIN_ID = #{LOGIN_ID}
   AND A.PWD = #{PWD}

