mapper.select:
/* S_COMM_USER */ SELECT 'id_' || (ROWNUM-1) AS "id" , A.LOGIN_ID AS LOGIN_ID , A.USER_NAME AS USER_NAME , A.USE_YN AS USE_YN , A.LOCK_YN AS LOCK_YN , TO_CHAR(A.CDT, 'YYYY-MM-DD') AS CDT , A.COMM_USER_ID AS COMM_USER_ID FROM COMM_USER A WHERE A.COMM_USER_ID = A.COMM_USER_ID
AND ( instr(A.LOGIN_ID, #{p_loginId}) > 0 OR instr(A.USER_NAME, #{p_loginId}) > 0 )
ORDER BY A.CDT DESC

mapper.update:
/* U_COMM_USER */
UPDATE COMM_USER
   SET USE_YN = #{USE_YN}
     , LOCK_YN = #{LOCK_YN}
   	 , MID = #{MID}
   	 , MDT = SYSDATE
   	 , MIP = #{MIP}
 WHERE COMM_USER_ID = #{COMM_USER_ID}
