mapper.sql:
		MERGE INTO COMM_GRID_CONTEXT A
    	USING DUAL
           ON (A.COMM_GRID_MASTER_ID = ${COMM_GRID_MASTER_ID} AND A.FUNCTION_CODE = ${FUNCTION_CODE})
    	 WHEN MATCHED THEN
			UPDATE SET A.USE_YN = ${USE_YN}
					 , A.DESCRIPTION = ${DESCRIPTION}
					 , A.MID = ${MID}
					 , A.MDT = SYSDATE
					 , A.MIP = ${MIP}
    	 WHEN NOT MATCHED THEN
        	INSERT (A.COMM_GRID_CONTEXT_ID, A.COMM_GRID_MASTER_ID, A.FUNCTION_CODE, A.USE_YN, A.DESCRIPTION, A.CID, A.CDT, A.CIP, A.MID, A.MDT, A.MIP)
        	VALUES (COMM_GRID_CONTEXT_S.NEXTVAL, ${COMM_GRID_MASTER_ID}, ${FUNCTION_CODE}, ${USE_YN}, ${DESCRIPTION}, ${MID}, SYSDATE, ${MIP}, ${MID}, SYSDATE, ${MIP})
